mapper.select:
SELECT a.ifcdSeq ,a.ifcdName ,a.ifcdOrder ,a.ifcdDelNy ,a.ifcgseq FROM nct.infrCode a WHERE 1=1
AND ifcdName LIKE concat('%',#{shValue},'%')
and ifcgSeq = #{shIfcgSeq}
limit #{startRnumForMysql},#{rowNumToShow}

mapper.insert:
INSERT INTO infrCodeGroup(
	ifcgName
	,ifcgDelNy
) VALUES(
	#{ifcgName}
	,0

)

SELECT last_insert_id()

mapper.update:
update 
	infrcodeGroup a
set 
	ifcgName=#{ifcgName}
where 
	a.ifcgSeq=#{ifcgSeq}

update 
	infrcode a
set 
	ifcdName=#{ifcdName}
where 
	a.ifcdSeq=#{ifcdSeq}
