mapper.select:
select a.ifmmSeq , a.ifmmName ,a.ifmmDob , concat(substring(b.ifmpNumber,1,3),"-",substring(b.ifmpNumber,4,4),"-",substring(b.ifmpNumber,7,4)) as ifmpPhone ,(select ifcdName from infrcode where ifmmGenderCd=ifcdseq) as ifmmGender ,c.ifmeEmailFull ,d.ifnaName ,a.ifmmPushConsentNy ,a.regDateTime from infrMember a left join infrmemberphone b on b.ifmmseq=a.ifmmseq and b.ifmpDefaultNy = 1 left join infrmemberEmail c on c.ifmmseq=a.ifmmseq and c.ifmeDefaultNy = 1
left join infrmembernationalty e on e.ifmmseq=a.ifmmseq and ifmnDefaultNy=1 left join infrnationalty d on d.ifnaseq=e.ifnaseq where 1=1 and ifmmDelNy=0
limit #{startRnumForMysql},#{rowNumToShow}

mapper.insert:
		insert into infrMember(
		 ifmmId
		,ifmmName
		,ifmmPassword
		,ifmmNameEng
		,ifmmGenderCd
		,ifmmDob
		,ifmmSmsConsentNy
		,ifmmDelNy
		,regDateTime

	) values (

		#{ifmmId}
		,#{ifmmName}
		,#{ifmmPassword}
		,#{ifmmNameEng}
		,#{ifmmGenderCd}
		,#{ifmmDob}
		,#{ifmmSmsConsentNy}
		,0
		,now()
	)

SELECT last_insert_id()

mapper.update:

update infrMember
set ifmmDelNy=1
where ifmmSeq=#{ifmmSeq}

update 
	infrmember a, infrMemberEmail b, infrmembernationalty c
set 

			 a.ifmmName = #{ifmmName}
			, a.ifmmId = #{ifmmId}


			,b.ifmeEmailFull = #{ifmeEmailFull}


where 
	a.ifmmSeq= #{ifmmSeq} 
	and b.ifmmSeq=#{ifmmSeq}
